import React from 'react';
import './WhatsPossible.css';

const items = [
  {
    title: 'Online Registration and Management',
    desc: 'Easy registration process with automated confirmation and tracking system.',
    icon: 'doc',
  },
  {
    title: 'Dynamic Event Management',
    desc: 'Create, edit, and manage events in real-time with intuitive controls.',
    icon: 'bolt',
    featured: true,
  },
  {
    title: 'Automated E-Certificates',
    desc: 'Generate and distribute digital certificates automatically upon event completion.',
    icon: 'cap',
  },
  {
    title: 'Secure Role-Based Access',
    desc: 'Protected admin panel with role-based permissions for enhanced security.',
    icon: 'lock',
  },
];

const Icon = ({ type }) => {
  const common = {
    width: 22,
    height: 22,
    viewBox: '0 0 24 24',
    fill: 'none',
    xmlns: 'http://www.w3.org/2000/svg',
  };
  switch (type) {
    case 'bolt':
      return (
        <svg {...common}>
          <path d="M13 2 3 14h7l-1 8 10-12h-7l1-8Z" fill="#FDBA74" stroke="#FB923C" strokeWidth="1.2"/>
        </svg>
      );
    case 'cap':
      return (
        <svg {...common}>
          <path d="M3 9.5 12 5l9 4.5-9 4.5L3 9.5Z" stroke="#a78bfa" strokeWidth="1.4"/>
          <path d="M7 12.5V16c1.5 1 3.2 1.5 5 1.5S15.5 17 17 16v-3.5" stroke="#a78bfa" strokeWidth="1.4"/>
        </svg>
      );
    case 'lock':
      return (
        <svg {...common}>
          <rect x="5" y="10" width="14" height="10" rx="2" stroke="#60a5fa" strokeWidth="1.4"/>
          <path d="M8 10V7a4 4 0 1 1 8 0v3" stroke="#60a5fa" strokeWidth="1.4"/>
        </svg>
      );
    default:
      return (
        <svg {...common}>
          <rect x="4" y="4" width="12" height="16" rx="2" stroke="#f472b6" strokeWidth="1.4"/>
          <path d="M8 8h6M8 12h6M8 16h4" stroke="#f472b6" strokeWidth="1.4"/>
        </svg>
      );
  }
};

const WhatsPossible = () => {
  return (
    <section className="whats-new-grid">
      {items.map((it) => (
        <article key={it.title} className={`wn-card ${it.featured ? 'wn-featured' : ''}`}>
          <div className="wn-icon">
            <div className="wn-blob" />
            <Icon type={it.icon} />
          </div>
          <h3 className="wn-title">{it.title}</h3>
          <p className="wn-desc">{it.desc}</p>
        </article>
      ))}
    </section>
  );
};

export default WhatsPossible;
