import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../utils/api';
import useAuthStore from '../store/authStore';
import './Register.css';

const Register = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated, user } = useAuthStore();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formError, setFormError] = useState('');

  // Fields to match the reference form
  const [fullName, setFullName] = useState('');
  const [mobile, setMobile] = useState('');
  const [email, setEmail] = useState('');
  const [gender, setGender] = useState('');
  const [organization, setOrganization] = useState('');
  const [address, setAddress] = useState('');
  const [remarks, setRemarks] = useState('');
  const [captcha, setCaptcha] = useState('');

  useEffect(() => {
    // Prevent admins from loading registration details
    if (isAuthenticated && user?.role === 'admin') {
      setLoading(false);
      return;
    }
    const load = async () => {
      try {
        const { data } = await api.get(`/api/events/${id}`);
        setEvent(data);
        // Prefill from user
        setFullName(user?.name || ((user?.email || '').split('@')[0]) || '');
        setEmail(user?.email || '');
      } catch (e) {
        const msg = e.response?.data?.message || e.message || 'Failed to load event';
        setError(msg);
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [id, isAuthenticated, user]);

  const constraints = useMemo(() => ({ mode: 'individual' }), []);

  const isPast = useMemo(() => {
    if (!event) return false;
    return new Date(event.date) < new Date();
  }, [event]);

  // No team-size syncing needed for individual reference form

  const validate = () => {
    if (!fullName.trim()) return 'Please enter name';
    if (!mobile.trim()) return 'Please enter mobile number';
    if (!email.trim()) return 'Please enter email';
    if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(email)) return 'Please enter a valid email';
    if (!organization.trim()) return 'Please enter organization';
    if (!captcha.trim()) return 'Please enter captcha';
    if (isPast) return 'Registration is closed for this event';
    return '';
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const v = validate();
    if (v) { setFormError(v); return; }
    try {
      // Map reference fields into existing API structure without breaking backend
      const payload = {
        eventId: id,
        department: gender || 'N/A',
        year: 'N/A',
        teamName: organization || fullName,
        teamMembers: [
          { name: fullName, email, phone: mobile },
        ],
        // Extra info can be logged server-side if supported
        meta: { address, remarks },
      };
      const { data } = await api.post('/api/registrations/register', payload);
      const registrationId = data?.registration?._id || data?.registrationId || 'pending';
      navigate(`/payment/${registrationId}`);
    } catch (err) {
      const msg = err.response?.data?.message || err.message || 'Registration failed';
      setFormError(msg);
    }
  };

  if (loading) return <div className="container"><p>Loading event…</p></div>;
  if (error) return <div className="container"><p>{error}</p></div>;
  if (isAuthenticated && user?.role === 'admin') {
    return (
      <div className="container">
        <h2>Registration not allowed</h2>
        <p>Admin accounts cannot register for events. You can add, edit, or delete events from the Admin panel.</p>
        <div className="form-actions">
          <button className="btn-primary" onClick={() => navigate('/admin')}>Go to Admin</button>
          <button className="btn-secondary" onClick={() => navigate(-1)}>Back</button>
        </div>
      </div>
    );
  }
  if (!event) return null;

  // Price calculation
  // - For main events: use basicRegistrationAmount
  // - For others: feePerHead (treated as total per team)
  const teamTotalFee = event?.isMainEvent
    ? Number(event?.basicRegistrationAmount || 0)
    : Number(event?.registrationDetails?.feePerHead || 0);

  // If main event does not have basic registration enabled, block UI
  if (event.isMainEvent && !event.basicRegistrationEnabled) {
    return (
      <div className="container">
        <h2>Registration Closed</h2>
        <p>Basic registration is not enabled for this main event.</p>
        <div className="form-actions">
          <button className="btn-secondary" onClick={() => navigate(-1)}>Back</button>
        </div>
      </div>
    );
  }

  const eventDateStr = event?.date ? new Date(event.date).toLocaleDateString() : '';

  return (
    <div className="container register-page">
      <div className="register-split">
        {/* Left hero panel */}
        <div className="register-left">
          <div className="illustration" aria-hidden>
            <div className="calendar-art" />
          </div>
          <div className="event-chip card">
            <div className="event-title">{event.name}</div>
            <div className="event-meta">{eventDateStr}{event?.location ? ` • ${event.location}` : ''}</div>
          </div>
        </div>

        {/* Right form panel */}
        <div className="register-right">
          <div className="form-header">
            <img
              className="brand"
              src="/images/gmrit_logo.png"
              alt="GMRIT"
              onError={(e) => { e.currentTarget.style.display = 'none'; }}
            />
            <h2>REGISTRATION FORM</h2>
          </div>

          {isPast && <p className="alert-warn">This event date has passed. Registration closed.</p>}
          {formError && <div className="alert-error">{formError}</div>}

          <form className="rg-form" onSubmit={onSubmit}>
            <div className="rg-grid">
              <label>Name *
                <input value={fullName} onChange={(e) => setFullName(e.target.value)} placeholder="Please enter name" />
              </label>

              <label>Mobile Number *
                <input value={mobile} onChange={(e) => setMobile(e.target.value)} placeholder="Please enter mobile number" />
              </label>

              <label>Email ID *
                <input value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Please enter email id" />
              </label>

              <label>Gender *
                <select value={gender} onChange={(e) => setGender(e.target.value)}>
                  <option value="">Please select</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </label>

              <label className="span-2">Name of the organization *
                <input value={organization} onChange={(e) => setOrganization(e.target.value)} placeholder="Please enter organization" />
              </label>

              <label className="span-1-2">Address
                <textarea rows={3} value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Please write address" />
              </label>

              <label className="span-1-2">Remarks, if any
                <textarea rows={3} value={remarks} onChange={(e) => setRemarks(e.target.value)} placeholder="Please write remarks" />
              </label>

              <label className="span-2">Please enter Captcha *
                <input value={captcha} onChange={(e) => setCaptcha(e.target.value)} placeholder="Enter shown captcha" />
              </label>
            </div>

            <div className="amount-row">
              <div className="amount">Amount: ₹{teamTotalFee} <span>(team total)</span></div>
            </div>

            <div className="form-actions">
              <button type="button" className="btn-secondary" onClick={() => navigate(-1)}>Cancel</button>
              <button type="submit" className="btn-primary" disabled={isPast}>Register Now</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
