import React, { useMemo, useRef, useState } from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

// A clean, responsive HTML certificate template rendered to PNG/PDF via html2canvas + jsPDF
// Props
// - participantName, eventName, dateText, organizerText, awardText
// - accentColor, primaryColor, bgPattern (optional url), sealText
// - downloadBaseName
const CertificateTemplate = ({
  participantName = 'Participant Name',
  teamName = '',
  eventName = 'Event Name',
  dateText = '',
  organizerText = '',
  awardText = 'Certificate of Participation',
  accentColor = '#0F172A',
  primaryColor = '#2563EB',
  bgPattern = '',
  sealText = 'Official',
  issuerName = 'GMRIT',
  logoUrl = '',
  headerLogoUrl = '',
  showSeal = true,
  sealSize = 110,
  downloadBaseName = 'certificate',
  showActions = true,
  // New professional elements
  certificateId = '',
  verifyUrl = '',
  orgName = '',
  orgTagline = '',
  courseMeta = { duration: '4 Hours', level: 'Advanced', credits: '' },
  eventTimings = null,
  leftSigner = { name: 'Organizer', title: 'Organizer / Signature' },
  rightSigner = { name: 'Registrar', title: 'Registrar / Authorized Signatory' },
  qrImageUrl, // if provided, overrides generated QR url
  showQR = false,
}) => {
  const ref = useRef(null);
  const [busy, setBusy] = useState(false);

  // Calculate duration from event timings
  const calculatedDuration = useMemo(() => {
    if (!eventTimings || !eventTimings.length) return courseMeta?.duration || '—';

    let totalMinutes = 0;
    eventTimings.forEach(timing => {
      if (timing.startTime && timing.endTime) {
        const start = parseTime(timing.startTime);
        const end = parseTime(timing.endTime);
        if (start && end) {
          const diff = (end - start) / (1000 * 60); // minutes
          totalMinutes += diff > 0 ? diff : 0;
        }
      }
    });

    if (totalMinutes === 0) return courseMeta?.duration || '—';

    const hours = Math.floor(totalMinutes / 60);
    const mins = Math.round(totalMinutes % 60);

    if (hours > 0 && mins > 0) return `${hours} Hour${hours > 1 ? 's' : ''} ${mins} Min${mins > 1 ? 's' : ''}`;
    if (hours > 0) return `${hours} Hour${hours > 1 ? 's' : ''}`;
    return `${mins} Minute${mins > 1 ? 's' : ''}`;
  }, [eventTimings, courseMeta]);

  // Helper to parse time strings like "10:00 AM" or "14:30"
  const parseTime = (timeStr) => {
    if (!timeStr) return null;
    try {
      const today = new Date();
      const [time, period] = timeStr.split(' ');
      let [hours, minutes] = time.split(':').map(Number);

      if (period) {
        // 12-hour format
        if (period.toUpperCase() === 'PM' && hours !== 12) hours += 12;
        if (period.toUpperCase() === 'AM' && hours === 12) hours = 0;
      }

      return new Date(today.getFullYear(), today.getMonth(), today.getDate(), hours, minutes || 0);
    } catch {
      return null;
    }
  };

  const styles = useMemo(() => ({
    wrapper: {
      position: 'relative',
      width: '1200px',
      maxWidth: '100%',
      aspectRatio: '1200 / 700',
      margin: '0 auto',
      background: '#1e293b',
      borderRadius: '20px',
      boxShadow: '0 20px 60px rgba(0,0,0,0.25)',
      overflow: 'hidden',
      padding: '24px',
    },
    panel: {
      position: 'relative',
      width: '100%',
      height: '100%',
      background: '#fff',
      borderRadius: '16px',
      padding: '48px 60px',
      display: 'flex',
      flexDirection: 'column',
      backgroundImage: bgPattern ? `url(${bgPattern})` : 'none',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      border: '3px solid #d4af37',
    },
    headerRow: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '32px',
    },
    leftHeader: {
      display: 'flex',
      alignItems: 'center',
      gap: '16px',
    },
    gmLogo: {
      width: '80px',
      height: '80px',
      background: 'linear-gradient(135deg, #eab308 0%, #f59e0b 100%)',
      borderRadius: '12px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 800,
      fontSize: '32px',
      color: '#fff',
      flexShrink: 0,
    },
    orgBlock: {
      display: 'flex',
      flexDirection: 'column',
      gap: '4px',
    },
    orgName: {
      fontWeight: 800,
      fontSize: '18px',
      color: '#111827',
      letterSpacing: '0.5px',
    },
    orgTagline: {
      fontSize: '13px',
      color: '#6B7280',
    },
    certId: {
      fontSize: '12px',
      color: '#9CA3AF',
      textAlign: 'right',
    },
    title: {
      fontFamily: 'Georgia, Times, serif',
      fontWeight: 700,
      fontSize: '38px',
      color: '#111827',
      textAlign: 'center',
      marginBottom: '24px',
      letterSpacing: '-0.5px',
    },
    certifyText: {
      fontSize: '15px',
      color: '#6B7280',
      textAlign: 'center',
      marginBottom: '16px',
    },
    name: {
      fontFamily: 'Georgia, Times, serif',
      fontWeight: 800,
      fontSize: '64px',
      color: '#111827',
      textAlign: 'center',
      lineHeight: 1.1,
      marginBottom: '8px',
      borderBottom: '3px solid #eab308',
      paddingBottom: '8px',
      display: 'inline-block',
      margin: '0 auto 16px',
    },
    team: {
      fontWeight: 700,
      fontSize: '20px',
      color: '#374151',
      textAlign: 'center',
      marginBottom: '24px',
    },
    subtitle: {
      fontSize: '16px',
      color: '#374151',
      textAlign: 'center',
      lineHeight: 1.6,
      marginBottom: '24px',
      maxWidth: '800px',
      margin: '0 auto 24px',
    },
    metaBlock: {
      display: 'flex',
      justifyContent: 'center',
      gap: '48px',
      marginBottom: '32px',
      paddingTop: '16px',
      borderTop: '1px solid #e5e7eb',
    },
    metaItem: {
      textAlign: 'center',
    },
    metaLabel: {
      fontSize: '13px',
      color: '#6B7280',
      marginBottom: '4px',
      fontWeight: 500,
    },
    metaValue: {
      fontSize: '18px',
      color: '#111827',
      fontWeight: 700,
    },
    footerRow: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr 1fr',
      gap: '32px',
      marginTop: 'auto',
      paddingTop: '24px',
    },
    footerItem: {
      textAlign: 'center',
    },
    footerName: {
      fontSize: '16px',
      color: '#111827',
      fontWeight: 600,
      marginBottom: '8px',
    },
    line: {
      height: '2px',
      background: '#9CA3AF',
      marginBottom: '8px',
    },
    label: {
      color: '#6B7280',
      fontSize: '13px',
      fontWeight: 500,
    },
    seal: {
      position: 'absolute',
      right: '80px',
      bottom: '80px',
      width: `${sealSize}px`,
      height: `${sealSize}px`,
      borderRadius: '50%',
      border: `4px solid ${primaryColor}`,
      color: primaryColor,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 800,
      fontSize: '15px',
      background: 'rgba(37, 99, 235, 0.06)',
      textTransform: 'uppercase',
    },
    issuerFooter: {
      position: 'absolute',
      bottom: '20px',
      right: '60px',
      fontSize: '11px',
      color: '#9CA3AF',
    },
  }), [primaryColor, bgPattern, sealSize]);

  const exportPNG = async () => {
    if (!ref.current) return;
    setBusy(true);
    try {
      const canvas = await html2canvas(ref.current, {
        backgroundColor: '#ffffff',
        scale: Math.min(2, window.devicePixelRatio || 1.5),
      });
      const link = document.createElement('a');
      link.download = `${downloadBaseName}.png`;
      link.href = canvas.toDataURL('image/png');
      link.click();
    } finally { setBusy(false); }
  };

  const exportPDF = async () => {
    if (!ref.current) return;
    setBusy(true);
    try {
      const canvas = await html2canvas(ref.current, {
        backgroundColor: '#ffffff',
        scale: Math.min(2, window.devicePixelRatio || 1.5),
      });
      const imgData = canvas.toDataURL('image/png');
      // Create landscape A4 PDF
      const pdf = new jsPDF({ orientation: 'landscape', unit: 'pt', format: 'a4' });
      const pageW = pdf.internal.pageSize.getWidth();
      const pageH = pdf.internal.pageSize.getHeight();
      // Fit image inside page with margin
      const margin = 24;
      const availableW = pageW - margin * 2;
      const availableH = pageH - margin * 2;
      const imgW = canvas.width;
      const imgH = canvas.height;
      const ratio = Math.min(availableW / imgW, availableH / imgH);
      const w = imgW * ratio;
      const h = imgH * ratio;
      const x = (pageW - w) / 2;
      const y = (pageH - h) / 2;
      pdf.addImage(imgData, 'PNG', x, y, w, h);
      pdf.save(`${downloadBaseName}.pdf`);
    } finally { setBusy(false); }
  };

  return (
    <div>
      <div ref={ref} style={styles.wrapper}>
        {/* Right brand band */}
        <div style={styles.rightBand}>
          {logoUrl ? (
            <img src={logoUrl} alt="logo" style={styles.bandLogo} />
          ) : (
            <div style={styles.bandLogoPlaceholder}>LOGO</div>
          )}
        </div>
        {/* White certificate panel */}
        <div style={styles.panel}>
          {/* Header & brand block */}
          <div style={styles.headerRow}>
            <div style={{display:'flex',alignItems:'center',gap:12}}>
              {headerLogoUrl ? (
                <img src={headerLogoUrl} alt="institution" style={styles.headerLogo} />
              ) : (
                <div style={{ width: 120, height: 48 }} />
              )}
              {orgName ? (
                <div style={styles.orgBlock}>
                  <div style={styles.headerText}>{orgName}</div>
                  {orgTagline && <div style={styles.headerTag}>{orgTagline}</div>}
                </div>
              ) : <div />}
            </div>
            <div />
            {certificateId ? <div style={styles.certId}>ID: {certificateId}</div> : <div />}
          </div>
          <div style={styles.title}>{awardText}</div>
          <div style={styles.name}>{participantName}</div>
          {teamName && <div style={styles.team}>Team: {teamName}</div>}
          <div style={styles.rightCol}>
            {/* empty right column for visual balance / future content */}
          </div>
          <div style={styles.subtitle}>has successfully participated in <strong>{eventName}</strong> and demonstrated exceptional commitment and teamwork throughout the event.</div>
          <div style={styles.issuer}>Issued by {issuerName}</div>
          {dateText && <div style={styles.issuedInline}>Issued: {dateText}</div>}
          <div style={styles.metaBlock}>
            {calculatedDuration && <span><strong>Duration:</strong> {calculatedDuration}</span>}
            {courseMeta?.level && <span><strong>Level:</strong> {courseMeta.level}</span>}
            {courseMeta?.credits && <span><strong>Credits:</strong> {courseMeta.credits}</span>}
          </div>
          <div style={styles.footerRow}>
            <div>
              <div style={{ fontSize: 18, color: '#111827' }}>{leftSigner?.name || organizerText || '—'}</div>
              <div style={styles.line} />
              <div style={styles.label}>{leftSigner?.title || 'Instructor / Director'}</div>
            </div>
            <div>
              <div style={{ fontSize: 18, color: '#111827' }}>{rightSigner?.name || '—'}</div>
              <div style={styles.line} />
              <div style={styles.label}>{rightSigner?.title || 'Registrar / Authorized Signatory'}</div>
            </div>
            <div>
              <div style={{ fontSize: 18, color: '#111827' }}>{dateText || '—'}</div>
              <div style={styles.line} />
              <div style={styles.label}>Date</div>
            </div>
          </div>
        </div>
        {showSeal && <div style={styles.seal}>{sealText}</div>}
        {showQR && (verifyUrl || qrImageUrl) && (
          <>
            <img alt="QR" style={styles.qr} src={qrImageUrl || `https://api.qrserver.com/v1/create-qr-code/?size=184x184&data=${encodeURIComponent(verifyUrl)}`} />
            <div style={styles.verify}>This certificate is digitally signed and verifiable at {new URL(verifyUrl).origin}/verify</div>
          </>
        )}
      </div>
      {showActions !== false && (
        <div className="form-actions" style={{ marginTop: 12, display: 'flex', gap: 8 }}>
          <button className="btn-primary" type="button" onClick={exportPDF} disabled={busy} style={{ display: 'none' }}>
            {busy ? 'Rendering…' : 'Official PDF (verifiable)'}
          </button>
          <button className="btn-secondary" type="button" onClick={exportPNG} disabled={busy} style={{ display: 'none' }}>
            {busy ? 'Rendering…' : 'Download PNG'}
          </button>
        </div>
      )}
    </div>
  );
}
;

export default CertificateTemplate;
