import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../utils/api';
import toast from 'react-hot-toast';

const PaymentRazorpay = () => {
  const { registrationId } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [registration, setRegistration] = useState(null);
  const [processing, setProcessing] = useState(false);

  useEffect(() => {
    const load = async () => {
      try {
        const { data } = await api.get(`/api/registrations/${registrationId}`);
        setRegistration(data.registration);
      } catch (e) {
        setError(e.response?.data?.message || e.message || 'Failed to load payment');
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [registrationId]);

  // Load Razorpay script
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://checkout.razorpay.com/v1/checkout.js';
    script.async = true;
    document.body.appendChild(script);
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handlePayment = async () => {
    try {
      setProcessing(true);

      // Create Razorpay order
      const { data } = await api.post('/api/razorpay/create-order', {
        registrationId
      });

      const options = {
        key: data.keyId, // Razorpay Key ID
        amount: data.amount, // Amount in paise
        currency: data.currency,
        name: 'EventPortal',
        description: `Payment for Event Registration`,
        order_id: data.orderId,
        handler: async function (response) {
          try {
            // Verify payment
            const verifyData = await api.post('/api/razorpay/verify-payment', {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              registrationId
            });

            if (verifyData.data.success) {
              toast.success('Payment successful!');
              navigate('/my-events');
            } else {
              toast.error('Payment verification failed');
            }
          } catch (err) {
            console.error('Verification error:', err);
            toast.error('Payment verification failed');
          }
        },
        prefill: {
          name: registration?.userId?.name || '',
          email: registration?.userId?.email || '',
          contact: registration?.userId?.phone || ''
        },
        theme: {
          color: '#14b8a6'
        },
        modal: {
          ondismiss: function() {
            setProcessing(false);
            toast.error('Payment cancelled');
          }
        }
      };

      const rzp = new window.Razorpay(options);
      rzp.open();
    } catch (err) {
      console.error('Payment error:', err);
      toast.error(err.response?.data?.message || 'Failed to initiate payment');
      setProcessing(false);
    }
  };

  if (loading) {
    return (
      <div className="container" style={{ padding: '60px 20px', textAlign: 'center' }}>
        <p>Loading payment details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container" style={{ padding: '60px 20px' }}>
        <div style={{ 
          background: '#fee2e2', 
          color: '#7f1d1d', 
          padding: '16px', 
          borderRadius: '8px',
          border: '1px solid #fecaca'
        }}>
          {error}
        </div>
        <div style={{ marginTop: '20px' }}>
          <button className="btn-secondary" onClick={() => navigate('/my-events')}>
            Back to My Events
          </button>
        </div>
      </div>
    );
  }

  if (!registration) return null;

  // Check if already paid
  if (registration.paymentStatus === 'completed') {
    return (
      <div className="container" style={{ padding: '60px 20px', textAlign: 'center' }}>
        <div style={{
          background: '#d1fae5',
          color: '#065f46',
          padding: '16px',
          borderRadius: '8px',
          border: '1px solid #6ee7b7',
          marginBottom: '20px'
        }}>
          âœ“ Payment already completed
        </div>
        <button className="btn-primary" onClick={() => navigate('/my-events')}>
          Go to My Events
        </button>
      </div>
    );
  }

  // Free registration
  if (registration.totalFee === 0) {
    return (
      <div className="container" style={{ padding: '60px 20px', textAlign: 'center' }}>
        <h2>Registration Confirmed</h2>
        <p>No payment required for this event.</p>
        <button className="btn-primary" onClick={() => navigate('/my-events')}>
          Go to My Events
        </button>
      </div>
    );
  }

  return (
    <div className="container" style={{ padding: '60px 20px', maxWidth: '600px', margin: '0 auto' }}>
      <h2 style={{ marginBottom: '24px' }}>Complete Payment</h2>

      <div style={{
        background: 'white',
        padding: '24px',
        borderRadius: '12px',
        boxShadow: '0 4px 16px rgba(0,0,0,0.08)',
        marginBottom: '24px'
      }}>
        <div style={{ marginBottom: '16px' }}>
          <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>
            Registration ID
          </div>
          <div style={{ fontSize: '16px', fontWeight: '600', color: '#111827' }}>
            {registrationId}
          </div>
        </div>

        <div style={{ marginBottom: '16px' }}>
          <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>
            Event
          </div>
          <div style={{ fontSize: '16px', fontWeight: '600', color: '#111827' }}>
            {registration.eventId?.name || 'Event'}
          </div>
        </div>

        {registration.teamName && (
          <div style={{ marginBottom: '16px' }}>
            <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>
              Team Name
            </div>
            <div style={{ fontSize: '16px', fontWeight: '600', color: '#111827' }}>
              {registration.teamName}
            </div>
          </div>
        )}

        <div style={{
          borderTop: '1px solid #e5e7eb',
          paddingTop: '16px',
          marginTop: '16px'
        }}>
          <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>
            Amount to Pay
          </div>
          <div style={{ fontSize: '32px', fontWeight: '800', color: '#14b8a6' }}>
            â‚¹{registration.totalFee}
          </div>
        </div>
      </div>

      <div style={{ display: 'flex', gap: '12px' }}>
        <button 
          className="btn-secondary" 
          onClick={() => navigate('/my-events')}
          disabled={processing}
        >
          Cancel
        </button>
        <button 
          className="btn-primary" 
          onClick={handlePayment}
          disabled={processing}
          style={{ flex: 1 }}
        >
          {processing ? 'Processing...' : 'Pay Now'}
        </button>
      </div>

      <div style={{
        marginTop: '24px',
        padding: '16px',
        background: '#f9fafb',
        borderRadius: '8px',
        fontSize: '13px',
        color: '#6b7280',
        textAlign: 'center'
      }}>
        ðŸ”’ Secure payment powered by Razorpay
      </div>
    </div>
  );
};

export default PaymentRazorpay;
