import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, PaymentElement, useElements, useStripe } from '@stripe/react-stripe-js';
import api from '../utils/api';
import toast from 'react-hot-toast';

// Defer creating the Stripe instance until we have a valid publishable key
const getStripePromise = (pk) => (pk ? loadStripe(pk) : null);

const CheckoutForm = ({ registration, clientSecret }) => {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    if (!stripe || !elements) return;
    setSubmitting(true);

    try {
      const { error, paymentIntent } = await stripe.confirmPayment({
        elements,
        confirmParams: {
          return_url: window.location.origin + '/my-events'
        },
        redirect: 'if_required',
      });

      if (error) {
        setMessage(error.message || 'Payment failed');
        setSubmitting(false);
      } else if (paymentIntent && paymentIntent.status === 'succeeded') {
        setMessage('Payment successful! Updating registration...');

        // Update payment status in backend
        try {
          await api.post(`/api/registrations/${registration._id}/confirm-payment`, {
            paymentIntentId: paymentIntent.id
          });

          setTimeout(() => {
            navigate('/my-events');
          }, 1000);
        } catch (err) {
          console.error('Failed to update payment status:', err);
          // Still navigate even if update fails
          setTimeout(() => {
            navigate('/my-events');
          }, 1000);
        }
      } else {
        setMessage('Payment processing');
        setSubmitting(false);
      }
    } catch (err) {
      console.error('Payment error:', err);
      setMessage('Payment failed. Please try again.');
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 600, margin: '0 auto' }}>
      <div style={{ 
        background: 'white', 
        padding: '24px', 
        borderRadius: '12px', 
        boxShadow: '0 4px 16px rgba(0,0,0,0.08)',
        marginBottom: '20px'
      }}>
        <h3 style={{ marginTop: 0, marginBottom: '16px', fontSize: '18px', fontWeight: '600' }}>
          Select Payment Method
        </h3>
        <PaymentElement options={{ 
          layout: 'tabs',
          paymentMethodOrder: ['card', 'upi', 'netbanking']
        }} />
      </div>
      {message && (
        <div style={{ 
          background: message.includes('successful') ? '#d1fae5' : '#fee2e2', 
          color: message.includes('successful') ? '#065f46' : '#7f1d1d', 
          padding: '12px 16px', 
          border: `1px solid ${message.includes('successful') ? '#6ee7b7' : '#fecaca'}`, 
          borderRadius: 8, 
          marginBottom: 16,
          fontWeight: '500'
        }}>
          {message}
        </div>
      )}
      <div style={{ display: 'flex', gap: '12px' }}>
        <button 
          type="button" 
          className="btn-secondary" 
          onClick={() => navigate('/my-events')}
          disabled={submitting}
        >
          Cancel
        </button>
        <button 
          type="submit" 
          className="btn-primary" 
          disabled={!stripe || submitting}
          style={{ flex: 1 }}
        >
          {submitting ? 'Processing…' : `Pay ₹${registration?.totalFee ?? ''}`}
        </button>
      </div>
      <div style={{
        marginTop: '20px',
        padding: '12px',
        background: '#f9fafb',
        borderRadius: '8px',
        fontSize: '13px',
        color: '#6b7280',
        textAlign: 'center'
      }}>
        🔒 Secure test payment powered by Stripe
      </div>
    </form>
  );
};

const Payment = () => {
  const { registrationId } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [registration, setRegistration] = useState(null);
  const [clientSecret, setClientSecret] = useState(null);
  const publishableKey = process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY;
  const stripePromise = useMemo(() => getStripePromise(publishableKey), [publishableKey]);

  useEffect(() => {
    const load = async () => {
      try {
        const { data } = await api.get(`/api/registrations/${registrationId}`);
        setRegistration(data.registration);
        setClientSecret(data.clientSecret);
      } catch (e) {
        setError(e.response?.data?.message || e.message || 'Failed to load payment');
      } finally {
        setLoading(false);
      }
    };
    load();
  }, [registrationId]);

  if (loading) return <div className="container"><p>Loading payment…</p></div>;
  if (error) return <div className="container"><p>{error}</p></div>;
  if (!registration) return null;

  // No payment required (free or below threshold)
  if (!clientSecret) {
    return (
      <div className="container">
        <h2>Payment</h2>
        <p>Registration ID: <strong>{registrationId}</strong></p>
        <p>No payment required for this registration.</p>
        <div className="form-actions">
          <button className="btn-primary" onClick={() => navigate('/my-events')}>Done</button>
        </div>
      </div>
    );
  }

  // If Stripe publishable key is missing, show a helpful message instead of crashing
  if (!publishableKey) {
    return (
      <div className="container">
        <h2>Payment</h2>
        <p>Registration ID: <strong>{registrationId}</strong></p>
        <div style={{ background:'#fff7ed', border:'1px solid #fed7aa', color:'#9a3412', padding:12, borderRadius:8, margin:'12px 0' }}>
          Missing Stripe publishable key. Please set <code>REACT_APP_STRIPE_PUBLISHABLE_KEY</code> in your frontend <code>.env</code> file and restart the dev server.
        </div>
        <div className="form-actions">
          <button className="btn-secondary" onClick={() => navigate('/my-events')}>Back</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container" style={{ padding: '60px 20px', maxWidth: '800px', margin: '0 auto' }}>
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <h2 style={{ fontSize: '32px', fontWeight: '800', marginBottom: '8px' }}>Complete Payment</h2>
        <p style={{ color: '#6b7280', fontSize: '16px' }}>Secure checkout powered by Stripe</p>
      </div>

      <div style={{
        background: 'white',
        padding: '24px',
        borderRadius: '12px',
        boxShadow: '0 4px 16px rgba(0,0,0,0.08)',
        marginBottom: '32px'
      }}>
        <div style={{ display: 'grid', gap: '16px' }}>
          <div>
            <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Event</div>
            <div style={{ fontSize: '16px', fontWeight: '600' }}>{registration?.eventId?.name || 'Event Registration'}</div>
          </div>
          <div>
            <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Registration ID</div>
            <div style={{ fontSize: '14px', fontFamily: 'monospace', color: '#374151' }}>{registrationId}</div>
          </div>
          {registration?.teamName && (
            <div>
              <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Team Name</div>
              <div style={{ fontSize: '16px', fontWeight: '600' }}>{registration.teamName}</div>
            </div>
          )}
          <div style={{ borderTop: '1px solid #e5e7eb', paddingTop: '16px', marginTop: '8px' }}>
            <div style={{ fontSize: '14px', color: '#6b7280', marginBottom: '4px' }}>Amount to Pay</div>
            <div style={{ fontSize: '36px', fontWeight: '800', color: '#14b8a6' }}>₹{registration?.totalFee}</div>
          </div>
        </div>
      </div>

      <Elements stripe={stripePromise} options={{ 
        clientSecret, 
        appearance: { 
          theme: 'stripe',
          variables: {
            colorPrimary: '#14b8a6',
            colorBackground: '#ffffff',
            colorText: '#1f2937',
            colorDanger: '#ef4444',
            fontFamily: 'system-ui, sans-serif',
            borderRadius: '8px'
          }
        } 
      }}>
        <CheckoutForm registration={registration} clientSecret={clientSecret} />
      </Elements>
    </div>
  );
};

export default Payment;
