import React, { useMemo, useRef, useState } from 'react';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

// A clean, responsive HTML certificate template rendered to PNG/PDF via html2canvas + jsPDF
// Props
// - participantName, eventName, dateText, organizerText, awardText
// - accentColor, primaryColor, bgPattern (optional url), sealText
// - downloadBaseName
const CertificateTemplate = ({
  participantName = 'Participant Name',
  eventName = 'Event Name',
  dateText = '',
  organizerText = '',
  awardText = 'Certificate of Participation',
  accentColor = '#0F172A',
  primaryColor = '#2563EB',
  bgPattern = '',
  sealText = 'Official',
  downloadBaseName = 'certificate',
}) => {
  const ref = useRef(null);
  const [busy, setBusy] = useState(false);

  const styles = useMemo(() => ({
    wrapper: {
      position: 'relative',
      width: '1200px',
      maxWidth: '100%',
      aspectRatio: '1200 / 850',
      background: '#fafafa',
      borderRadius: '16px',
      boxShadow: '0 10px 30px rgba(0,0,0,0.12)',
      overflow: 'hidden',
      border: '8px solid #111827',
    },
    headerBand: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      height: '120px',
      background: `linear-gradient(90deg, ${primaryColor}, ${accentColor})`,
    },
    content: {
      position: 'absolute',
      inset: '24px',
      background: '#fff',
      borderRadius: '10px',
      padding: '48px 56px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'space-between',
      textAlign: 'center',
      backgroundImage: bgPattern ? `url(${bgPattern})` : 'none',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    title: {
      fontFamily: 'Georgia, Times, serif',
      fontWeight: 800,
      fontSize: '46px',
      color: accentColor,
      letterSpacing: '0.5px',
      marginTop: '8px',
      textShadow: '0 1px 0 rgba(0,0,0,0.12)',
    },
    name: {
      marginTop: '24px',
      fontFamily: 'Georgia, Times, serif',
      fontWeight: 800,
      fontSize: '64px',
      color: '#111827',
    },
    subtitle: {
      marginTop: '16px',
      fontSize: '20px',
      color: '#374151',
    },
    footerRow: {
      display: 'grid',
      gridTemplateColumns: '1fr 1fr',
      width: '100%',
      alignItems: 'end',
      gap: '24px',
      marginTop: '48px',
    },
    line: {
      height: '2px',
      background: '#9CA3AF',
      marginTop: '12px',
    },
    label: {
      color: '#6B7280',
      marginTop: '6px',
      fontSize: '16px',
    },
    seal: {
      position: 'absolute',
      right: '48px',
      bottom: '48px',
      width: '140px',
      height: '140px',
      borderRadius: '50%',
      border: `6px solid ${primaryColor}`,
      color: primaryColor,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 800,
      fontSize: '18px',
      background: 'rgba(37, 99, 235, 0.08)'
    },
  }), [primaryColor, accentColor, bgPattern]);

  const exportPNG = async () => {
    if (!ref.current) return;
    setBusy(true);
    try {
      const canvas = await html2canvas(ref.current, {
        backgroundColor: '#ffffff',
        scale: Math.min(2, window.devicePixelRatio || 1.5),
      });
      const link = document.createElement('a');
      link.download = `${downloadBaseName}.png`;
      link.href = canvas.toDataURL('image/png');
      link.click();
    } finally { setBusy(false); }
  };

  const exportPDF = async () => {
    if (!ref.current) return;
    setBusy(true);
    try {
      const canvas = await html2canvas(ref.current, {
        backgroundColor: '#ffffff',
        scale: Math.min(2, window.devicePixelRatio || 1.5),
      });
      const imgData = canvas.toDataURL('image/png');
      // Create landscape A4 PDF
      const pdf = new jsPDF({ orientation: 'landscape', unit: 'pt', format: 'a4' });
      const pageW = pdf.internal.pageSize.getWidth();
      const pageH = pdf.internal.pageSize.getHeight();
      // Fit image inside page with margin
      const margin = 24;
      const availableW = pageW - margin * 2;
      const availableH = pageH - margin * 2;
      const imgW = canvas.width;
      const imgH = canvas.height;
      const ratio = Math.min(availableW / imgW, availableH / imgH);
      const w = imgW * ratio;
      const h = imgH * ratio;
      const x = (pageW - w) / 2;
      const y = (pageH - h) / 2;
      pdf.addImage(imgData, 'PNG', x, y, w, h);
      pdf.save(`${downloadBaseName}.pdf`);
    } finally { setBusy(false); }
  };

  return (
    <div>
      <div ref={ref} style={styles.wrapper}>
        <div style={styles.headerBand} />
        <div style={styles.content}>
          <div>
            <div style={styles.title}>{awardText}</div>
            <div style={styles.name}>{participantName}</div>
            <div style={styles.subtitle}>for successfully participating in {eventName}</div>
          </div>
          <div style={styles.footerRow}>
            <div>
              <div style={{ fontSize: 22, color: '#111827' }}>{dateText || '—'}</div>
              <div style={styles.line} />
              <div style={styles.label}>Date</div>
            </div>
            <div>
              <div style={{ fontSize: 20, color: '#111827' }}>{organizerText || '—'}</div>
              <div style={styles.line} />
              <div style={styles.label}>Organizer / Signature</div>
            </div>
          </div>
          <div style={styles.seal}>{sealText}</div>
        </div>
      </div>
      <div className="form-actions" style={{ marginTop: 12, display: 'flex', gap: 8 }}>
        <button className="btn-primary" type="button" onClick={exportPNG} disabled={busy}>
          {busy ? 'Rendering…' : 'Download PNG'}
        </button>
        <button className="btn-secondary" type="button" onClick={exportPDF} disabled={busy}>
          {busy ? 'Rendering…' : 'Download PDF'}
        </button>
      </div>
    </div>
  );
};

export default CertificateTemplate;
