import React, { useEffect, useRef, useState } from 'react';
import useAuthStore from '../store/authStore';
import fetchWithLoading from '../utils/fetcher';
import SkeletonCard from './SkeletonCard/SkeletonCard';
import './LiveEvents.css';

const LiveEvents = () => {
  const { isAuthenticated, user } = useAuthStore();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [status, setStatus] = useState('upcoming');
  const [timeframe, setTimeframe] = useState('all');
  const [filtersOpen, setFiltersOpen] = useState(false);
  const popRef = useRef(null);
  const [expandedId, setExpandedId] = useState(null);

  // Helper to get combined event Date from `ev.date` and optional `ev.time`
  const getEventDateTime = (ev) => {
    try {
      const dateStr = ev?.date || ev?.startDate || ev?.eventDate;
      const timeStr = (ev?.time || ev?.startTime || '').trim();
      if (!dateStr) return null;
      // Try to build an ISO-like string if time is present
      let d = new Date(dateStr);
      if (timeStr) {
        // If time is like "02:00 PM", parse it
        const m = timeStr.match(/^(\d{1,2}):(\d{2})\s*(AM|PM)?/i);
        if (m) {
          let h = parseInt(m[1], 10);
          const min = parseInt(m[2], 10);
          const ap = (m[3] || '').toUpperCase();
          if (ap === 'PM' && h < 12) h += 12;
          if (ap === 'AM' && h === 12) h = 0;
          d = new Date(d.getFullYear(), d.getMonth(), d.getDate(), h, min, 0, 0);
        }
      }
      return isNaN(d.getTime()) ? null : d;
    } catch {
      return null;
    }
  };

  useEffect(() => {
    const params = new URLSearchParams();
    params.set('status', status);
    if (status === 'upcoming' && ['today', 'this_week', 'this_month'].includes(timeframe)) {
      params.set('timeframe', timeframe);
    }
    setLoading(true);
    fetchWithLoading(`/api/events?${params.toString()}`)
      .then((r) => r.json())
      .then((data) => setEvents(Array.isArray(data) ? data : []))
      .catch(() => setEvents([]))
      .finally(() => setLoading(false));
  }, [status, timeframe]);

  // Close filters popover on outside click
  useEffect(() => {
    const onDocClick = (e) => {
      if (!popRef.current) return;
      if (!popRef.current.contains(e.target)) setFiltersOpen(false);
    };
    if (filtersOpen) document.addEventListener('click', onDocClick);
    return () => document.removeEventListener('click', onDocClick);
  }, [filtersOpen]);

  if (!isAuthenticated) return null;

  return (
    <section className="live-events">
      <div className="section-header">
        <h2>Live Events</h2>
        <div className="filters" style={{ position: 'relative' }} ref={popRef}>
          <button className="filter-btn" onClick={() => setFiltersOpen((v) => !v)} aria-haspopup="true" aria-expanded={filtersOpen} title="Filters">
            {/* Funnel icon (inline SVG) */}
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" style={{marginRight:6}}>
              <path d="M3 5h18l-7 8v5l-4 2v-7L3 5z" stroke="currentColor" strokeWidth="1.5" fill="none"/>
            </svg>
            Filter
          </button>
          {filtersOpen && (
            <div className="filter-popover">
              <div className="filter-group">
                <div className="filter-title">Status</div>
                <label><input type="radio" name="status" value="upcoming" checked={status==='upcoming'} onChange={(e)=>setStatus(e.target.value)} /> Upcoming</label>
                <label><input type="radio" name="status" value="live" checked={status==='live'} onChange={(e)=>setStatus(e.target.value)} /> Live</label>
                <label><input type="radio" name="status" value="ended" checked={status==='ended'} onChange={(e)=>setStatus(e.target.value)} /> Ended</label>
              </div>
              {status !== 'live' && (
                <div className="filter-group">
                  <div className="filter-title">Timeframe</div>
                  <label><input type="radio" name="timeframe" value="all" checked={timeframe==='all'} onChange={(e)=>setTimeframe(e.target.value)} /> All</label>
                  <label><input type="radio" name="timeframe" value="today" checked={timeframe==='today'} onChange={(e)=>setTimeframe(e.target.value)} /> Today</label>
                  <label><input type="radio" name="timeframe" value="this_week" checked={timeframe==='this_week'} onChange={(e)=>setTimeframe(e.target.value)} /> This Week</label>
                  <label><input type="radio" name="timeframe" value="this_month" checked={timeframe==='this_month'} onChange={(e)=>setTimeframe(e.target.value)} /> This Month</label>
                </div>
              )}
              <div style={{display:'flex', justifyContent:'flex-end', gap:8, marginTop:8}}>
                <button className="btn-secondary" onClick={() => setFiltersOpen(false)}>Done</button>
              </div>
            </div>
          )}
        </div>
      </div>
      <div className="events-list">
        {loading && (
          <div className="skeleton-list">
            {Array.from({ length: 3 }).map((_, i) => <SkeletonCard key={`sk-live-${i}`} />)}
          </div>
        )}

        {!loading && events.length > 0 && (
          <div className="events-table" role="list">
            <div className="events-header">
              <div className="col name">Event</div>
              <div className="col dept">Department</div>
              <div className="col dt">Date & Time</div>
              <div className="col action">Action</div>
            </div>
            {events
              .slice()
              .sort((a, b) => {
                const da = getEventDateTime(a)?.getTime() ?? 0;
                const db = getEventDateTime(b)?.getTime() ?? 0;
                return da - db; // ascending
              })
              .map((ev) => {
                const dt = getEventDateTime(ev);
                const isPast = dt ? dt.getTime() < Date.now() : false;
                const expanded = expandedId === ev._id;
                return (
                  <div className="events-row" key={ev._id} role="listitem">
                    <div className="row-main">
                      <button
                        className="col name name-btn"
                        onClick={() => setExpandedId(expanded ? null : ev._id)}
                        aria-expanded={expanded}
                        title="Show details"
                      >
                        <span className="caret" aria-hidden>▸</span>
                        {ev.name}
                      </button>
                      <div className="col dept">{ev.department || ev.dept || '—'}</div>
                      <div className="col dt">{dt ? dt.toLocaleString() : `${new Date(ev.date).toLocaleDateString()} ${ev.time || ''}`}</div>
                      <div className="col action">
                        {isPast ? (
                          <button className="btn btn-closed" disabled>Registration Closed</button>
                        ) : (
                          <a className="btn btn-primary" href={`/events/${ev._id}/register`}>Register</a>
                        )}
                      </div>
                    </div>
                    <div className={`row-details ${expanded ? 'open' : ''}`}>
                      <div className="details-inner">
                        <div className="details-media">
                          <img src={ev.imageUrl || 'https://via.placeholder.com/480x320?text=Event'} alt={ev.name} />
                        </div>
                        <div className="details-content">
                          <h4>{ev.name}</h4>
                          {ev.description && <p className="desc">{ev.description}</p>}
                          <div className="meta">
                            <div><strong>Department:</strong> {ev.department || '—'}</div>
                            <div><strong>Date & Time:</strong> {dt ? dt.toLocaleString() : `${new Date(ev.date).toLocaleDateString()} ${ev.time || ''}`}</div>
                            {ev.location && <div><strong>Venue:</strong> {ev.location}</div>}
                            {ev.fee && <div><strong>Fee:</strong> ₹{ev.fee}</div>}
                          </div>
                          <div className="details-actions">
                            {isPast ? (
                              <button className="btn btn-closed" disabled>Registration Closed</button>
                            ) : (
                              <a className="btn btn-primary" href={`/events/${ev._id}/register`}>Register</a>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
          </div>
        )}

        {!loading && events.length === 0 && <p>No events found.</p>}
      </div>
    </section>
  );
};

export default LiveEvents;
