import React, { useEffect } from 'react';
import api from '../utils/api';
import useAuthStore from '../store/authStore';
import toast from 'react-hot-toast';
import '../components/SkeletonCard/SkeletonCard.css';
import './Profile.css';

const Profile = () => {
  const { user, setUser } = useAuthStore();
  const [form, setForm] = React.useState({
    name: '',
    email: '',
    gender: 'Prefer not to say',
    year: '',
    department: '',
    currentPassword: '',
    newPassword: ''
  });
  const [isEditing, setIsEditing] = React.useState(false);
  const [loading, setLoading] = React.useState(true);

  useEffect(() => {
    const load = async () => {
      try {
        setLoading(true);
        const { data } = await api.get('/api/auth/me');
        setUser(data);
        setForm((f) => ({
          ...f,
          name: data.name || '',
          email: data.email || '',
          gender: data.gender || 'Prefer not to say',
          year: data.year || '',
          department: data.department || ''
        }));
      } catch {}
      finally {
        setLoading(false);
      }
    };
    load();
  }, [setUser]);

  // derived display helpers
  const initials = (user?.name || user?.email || '?')
    .split(' ')
    .map((s) => s[0])
    .join('')
    .slice(0, 2)
    .toUpperCase();

  const completion = (() => {
    const fields = ['name', 'email', 'gender', 'year', 'department'];
    const filled = fields.filter((k) => !!(user && String(user[k] || '').trim())).length;
    return Math.round((filled / fields.length) * 100);
  })();

  const [showDeptChangeRequest, setShowDeptChangeRequest] = React.useState(false);
  const [deptChangeReason, setDeptChangeReason] = React.useState('');

  return (
    <div className="profile-page">
      {/* Header banner */}
      <div className="profile-header profile-fade">
        <div className="profile-banner">
          <div>
            <h2 className="banner-title">{user?.name || 'Your Profile'}</h2>
            <p className="banner-sub">{(user?.role ? user.role.charAt(0).toUpperCase()+user.role.slice(1) : 'Student')} {user?.year ? `| ${user.year}` : ''}</p>
          </div>
        </div>
        <div className="profile-avatar" aria-hidden>
          {user?.avatarUrl || user?.photoUrl ? (
            <img src={user.avatarUrl || user.photoUrl} alt="Profile avatar" />
          ) : (
            <div className="initials">{initials}</div>
          )}
        </div>
      </div>

      {/* Body */}
      {loading && (
        <div className="profile-body">
          <div className="card" style={{ padding: 16 }}>
            <div className="skeleton-line skeleton-line-lg" style={{ width: '30%', height: 24 }} />
            <div className="skeleton-line" style={{ width: '70%', marginTop: 12 }} />
            <div className="skeleton-line" style={{ width: '60%' }} />
            <div className="skeleton-line" style={{ width: '50%' }} />
            <div className="skeleton-line" style={{ width: '40%' }} />
          </div>
        </div>
      )}
      {!loading && user && !isEditing && (
        <div className="profile-body profile-fade">
          <div className="profile-card">
            <div className="profile-meta">
              <div>Last updated on {new Date().toLocaleDateString()}</div>
              <div className="progress"><span style={{ width: `${completion}%` }} /></div>
            </div>
            <div className="grid">
              <div className="right">
                <div className="profile-field"><div className="label"><span className="icon">👤</span>Name</div><div className="value">{user.name || '-'}</div></div>
                <div className="profile-field"><div className="label"><span className="icon">📧</span>Email</div><div className="value">{user.email}</div></div>
                <div className="profile-field"><div className="label"><span className="icon">👩</span>Gender</div><div className="value">{user.gender || 'Prefer not to say'}</div></div>
                <div className="profile-field"><div className="label"><span className="icon">🎓</span>Year</div><div className="value">{user.year || '-'}</div></div>
                <div className="profile-field">
                  <div className="label"><span className="icon">🏛️</span>Department</div>
                  <div className="value">
                    {user.department || '-'}
                    {user.department && (
                      <button 
                        onClick={() => setShowDeptChangeRequest(!showDeptChangeRequest)}
                        style={{
                          marginLeft: 8,
                          padding: '4px 8px',
                          fontSize: '0.75rem',
                          background: '#f59e0b',
                          color: 'white',
                          border: 'none',
                          borderRadius: 4,
                          cursor: 'pointer'
                        }}
                      >
                        Request Change
                      </button>
                    )}
                  </div>
                </div>
                {showDeptChangeRequest && user.department && (
                  <div style={{
                    background: '#fef3c7',
                    border: '2px solid #f59e0b',
                    borderRadius: 8,
                    padding: 12,
                    marginTop: 8
                  }}>
                    <strong>Request Department Change</strong>
                    <p style={{fontSize: '0.9rem', margin: '8px 0'}}>Please provide a reason for changing your department. An admin will review your request.</p>
                    <textarea 
                      value={deptChangeReason}
                      onChange={(e) => setDeptChangeReason(e.target.value)}
                      placeholder="Reason for department change..."
                      style={{
                        width: '100%',
                        minHeight: 80,
                        padding: 8,
                        borderRadius: 4,
                        border: '1px solid #d1d5db',
                        marginBottom: 8
                      }}
                    />
                    <div style={{display: 'flex', gap: 8}}>
                      <button 
                        onClick={async () => {
                          if (!deptChangeReason.trim()) {
                            toast.error('Please provide a reason');
                            return;
                          }
                          try {
                            await api.post('/api/auth/request-department-change', {
                              reason: deptChangeReason
                            });
                            toast.success('Request submitted successfully');
                            setShowDeptChangeRequest(false);
                            setDeptChangeReason('');
                          } catch (err) {
                            toast.error(err.response?.data?.msg || 'Failed to submit request');
                          }
                        }}
                        style={{
                          padding: '8px 16px',
                          background: '#10b981',
                          color: 'white',
                          border: 'none',
                          borderRadius: 6,
                          cursor: 'pointer',
                          fontWeight: 600
                        }}
                      >
                        Submit Request
                      </button>
                      <button 
                        onClick={() => {
                          setShowDeptChangeRequest(false);
                          setDeptChangeReason('');
                        }}
                        style={{
                          padding: '8px 16px',
                          background: '#6b7280',
                          color: 'white',
                          border: 'none',
                          borderRadius: 6,
                          cursor: 'pointer'
                        }}
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}
                <div className="profile-actions">
                  <button className="btn-edit-profile" onClick={async ()=>{
                    try {
                      const { data } = await api.get('/api/auth/me');
                      setUser(data);
                      setForm({
                        name: data.name || '',
                        email: data.email || '',
                        gender: data.gender || 'Prefer not to say',
                        year: data.year || '',
                        department: data.department || '',
                        currentPassword: '',
                        newPassword: ''
                      });
                      setIsEditing(true);
                    } catch {
                      toast.error('Failed to load latest details');
                    }
                  }}>✏️ Edit Details</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {!loading && user && isEditing && (
        <form className="profile-body profile-card" style={{display:'grid',gap:12}} onSubmit={async (e)=>{
          e.preventDefault();
          try {
            const { data } = await api.put('/api/auth/me', {
              name: form.name,
              email: form.email,
              gender: form.gender,
              year: form.year,
              department: form.department || undefined,
              currentPassword: form.currentPassword || undefined,
              newPassword: form.newPassword || undefined,
            });
            setUser(data.user);
            setForm((f)=>({ ...f, currentPassword:'', newPassword:'' }));
            toast.success('Profile updated');
            setIsEditing(false);
          } catch (err) {
            toast.error(err.response?.data?.msg || 'Update failed');
          }
        }}>
          <label>Name</label>
          <input value={form.name} onChange={(e)=>setForm({...form,name:e.target.value})} placeholder="e.g., John Doe" />

          <label>Email</label>
          <input value={form.email} onChange={(e)=>setForm({...form,email:e.target.value})} type="email" required />

          <label>Gender</label>
          <select value={form.gender} onChange={(e)=>setForm({...form,gender:e.target.value})}>
            <option>Male</option>
            <option>Female</option>
            <option>Other</option>
            <option>Prefer not to say</option>
          </select>

          <label>Year</label>
          <select value={form.year} onChange={(e)=>setForm({...form,year:e.target.value})}>
            <option value="">Select Year</option>
            <option value="First Year">First Year</option>
            <option value="Second Year">Second Year</option>
            <option value="Third Year">Third Year</option>
            <option value="Fourth Year">Fourth Year</option>
          </select>

          <label>Department</label>
          {user.department ? (
            <div style={{
              padding: 12,
              background: '#f3f4f6',
              borderRadius: 8,
              border: '1px solid #d1d5db'
            }}>
              <strong>{user.department}</strong>
              <p style={{fontSize: '0.85rem', color: '#6b7280', margin: '4px 0 0 0'}}>
                Department cannot be changed here. Use "Request Change" button above if needed.
              </p>
            </div>
          ) : (
            <select value={form.department} onChange={(e)=>setForm({...form,department:e.target.value})} required>
              <option value="">Select Department</option>
              <option value="CSE">CSE</option>
              <option value="IT">IT</option>
              <option value="ECE">ECE</option>
              <option value="EEE">EEE</option>
              <option value="CIVIL">CIVIL</option>
              <option value="MECH">MECH</option>
            </select>
          )}

          <div style={{borderTop:'1px solid #eee', marginTop:8, paddingTop:8}}>
            <strong>Change Password (optional)</strong>
          </div>
          <label>Current Password</label>
          <input type="password" value={form.currentPassword} onChange={(e)=>setForm({...form,currentPassword:e.target.value})} />
          <label>New Password</label>
          <input type="password" value={form.newPassword} onChange={(e)=>setForm({...form,newPassword:e.target.value})} />

          <div style={{display:'flex',gap:12,marginTop:8,justifyContent:'flex-end'}}>
            <button className="btn-primary" type="submit">Save Changes</button>
            <button type="button" className="btn-secondary" onClick={()=>{
              setIsEditing(false);
              setForm((f)=>({ ...f, currentPassword:'', newPassword:'' }));
            }}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default Profile;
